mapper.select:
SELECT
FROM T_SYS_ROLE A
ORDER BY A.CREATE_DT DESC limit #{offset}, #{pageSize}

mapper.delete:
DELETE FROM t_sys_role_menu WHERE role_id=#{id};
DELETE FROM t_sys_role_permission WHERE role_id=#{id};
DELETE FROM t_sys_role WHERE id=#{id}

mapper.insert:
INSERT INTO t_sys_role_menu(

		id,
		role_id,
		menu_id

)
VALUES(

		#{id},
		#{roleId},
		#{menuId}

)

INSERT INTO t_sys_role_permission(

		id,
		role_id,
		permission_id

)
VALUES(

		#{id},
		#{roleId},
		#{permissionId}

)
